function wound_drag_click()
enableTimer("wound_drag_window")
end
function wound_drag_release()
disableTimer("wound_drag_window")
end
function wound_resize_click()
windowpositions.wound_start_x, windowpositions.wound_start_y = getMousePosition()
enableTimer("wound_resize_window")
end
function wound_resize_release()
disableTimer("wound_resize_window")
windowpositions.wound_width = wound_new_width
windowpositions.wound_height = wound_new_height
table.save(getMudletHomeDir() .. "/windowpositions", windowpositions)
end
wound_resize_label:setClickCallback("wound_resize_click")
wound_resize_label:setReleaseCallback("wound_resize_release")
wound_drag_move:setClickCallback("wound_drag_click")
wound_drag_move:setReleaseCallback("wound_drag_release")
wound_container:show()

function wound_update()
head_color = wound_tracker.h.colour
chest_color = wound_tracker.c.colour
